INSERT INTO `bandas` (`id`, `datas_id`, `nome`, `url_imagem`, `descricao`) VALUES
(04, 01, 'Guns N'' Roses', 'http://www.rocknews-2003.blogger.com.br/logo-guns.gif', 'Guns N'' Roses (às vezes abreviado como G N'' R ou GnR) é uma banda de hard rock formada em Los Angeles, Califórnia (EUA), em 1985. A banda já lançou seis álbuns de estúdio, três EPs e um álbum ao vivo. O co-fundador e líder Axl Rose e o tecladista Dizzy Reed são os únicos membros da formação clássica no atual Guns N'' Roses.'),
(05, 01, 'Skank', 'http://3.bp.blogspot.com/-_vDghezf6dY/TZUEi3fohPI/AAAAAAAAD4k/QNLoH7CYft4/s400/skank_logo01.jpg', 'Skank é uma banda brasileira de reggae e pop rock formada por Samuel Rosa (guitarra e voz), Henrique Portugal (teclados), Lelo Zaneti (baixo) e Haroldo Ferretti (bateria), em março de 1991 em Belo Horizonte. A banda já vendeu mais de 6 milhões de discos entre CDs e DVDs.'),
(06, 01, 'Avenged Sevenfold', 'data:image/png;base64,', ' Avenged Sevenfold - Avenged Sevenfold (também conhecida como A7X) é uma banda norte-americana de metal de Huntington Beach, Califórnia.\r\nMembros: M.	 Shadows(vocal), Synyster Gates(guitarra), Zacky Vengeance(guitarra rítmica, backing vocal), Johnny Christ(baixo) e Arin Ilejay( bateria).'),
(07, 01, 'Capital Inicial', 'http://dc586.4shared.com/img/wXpph9y7/s3/13810caceb8/capital_inicial', 'Capital Inicial é uma banda de rock brasileira formada em Brasília, Distrito Federal, em 1982, depois que o grupo Aborto Elétrico encerrou as atividades, dando início também à banda Legião Urbana. A banda é composta pelo vocalista Dinho Ouro Preto, o baixista Flávio Lemos, seu irmão o baterista Fê Lemos, o guitarrista Yves Passarell '),
(08, 02, 'System of a Down', 'data:image/jpeg;base64,', 'O System Of a Down é uma banda de metal californiana formada pelo quarteto de descendentes de armênios Serj Tankian (vocal), Shavo Odadjian (baixo), Daron Malakian (guitarra) e John Dolmayan (bateria).'),
(09, 02, 'Bring me the Horizon', 'http://images.fanpop.com/images/image_uploads/BMTH-Logo-bring-me-the-horizon-714343_1920_976.jpg', 'Bring Me The Horizon ou BMTH é uma banda de Deathcore, Metalcore e Hardcore de Sheffield, Yorkshire. Seguem um estilo de música com muitas influências do death metal, o deathcore (Que é a fusão do death metal com o hardcore - estilo que inclui os vocais rasgados e berrados, agudos ou graves).'),
(10, 02, 'Black Sabbath', 'http://www.tintin.ch/pickcollection/AAABandlogos/black_sabbath_logo.jpg', 'Black Sabbath é considerada uma das bandas pioneiras no heavy metal junto ao Deep Purple e o Led Zeppelin, tendo influência crucial no desenvolvimento e definição do estilo.'),
(11, 02, 'NX Zero', 'http://www.rogerfires.com/content/5.design/62.nxzerologo/1.jpg', 'NX Zero (mais conhecido pelo nome artístico NX) é uma banda brasileira de emocore formada em 2001 na cidade de São Paulo, composta por Diego Ferrero (vocal), Leandro Rocha (guitarra e segunda voz), Daniel Weksler (bateria), Conrado Grandino (baixo) e Filipe Ricardo (guitarra).'),
(12, 03, 'METALLICA', 'https://encrypted-tbn1.gstatic.com/images?q=tbn:ANd9GcThM-Q6o6qe1am4dD80KbpNvXa3YjGOP95VbpKWqRDAVaXlvo4d4g', 'Metallica é uma banda norte-americana de metal originaria de Los Angeles, mas com base em San Francisco. Os seus lançamentos incluem tempos rápidos, instrumentais, e musicalidade agressiva, a qual os colocou no lugar de uma das bandas fundadoras do Big Four of Thrash, conjuntamente com Slayer, Megadeth e Anthrax.'),
(13, 03, 'Slayer', 'data:image/jpeg;base64,', 'O estilo musical de Slayer envolve rápidos tremolos, variados e complexos solos de guitarra, riffs em escalas irregulares, bumbo duplo na bateria e vocais pesados alternando desde falsettos até guturais. As letras e arte dos álbuns se baseiam em temas como serial killers, satanismo, religião e a guerra, o que levou a gerar diversos problemas ao longo da história da banda, como proibições de álbuns, atrasos em turnês, processos judiciais, além de ser constante alvo de críticas de grupos religiosos. Contudo, sua música tornou-se altamente influente, sendo frequentemente citada por muitas bandas como uma influência musical, visual e lírica.'),
(14, 03, 'Paul McCartney', 'http://www.freemalaysiatoday.com/wp-content/uploads/2014/09/Paul-McCartney-010.jpg', 'McCartney alcançou fama mundial como membro da banda de rock britânica The Beatles, com John Lennon, George Harrison e Ringo Starr.Paul McCartney é o canhoto e baixista mais famoso da história do rock, embora também toque outros instrumentos, como bateria, piano, guitarra, teclado, etc.'),
(15, 03, 'CPM 22', 'http://files.acervodorock.webnode.pt/200000348-742a77523a/cpm22logo.jpg', 'CPM 22 é uma banda brasileira de hardcore melódico formada em 1995 na cidade de Barueri, São Paulo.'),
(16, 05, 'AC/DC', 'data:image/jpeg;base64,', 'AC/DC é uma banda de rock formada em Sydney, Austrália em 1973 pelos irmãos Angus e Malcolm Young. A banda é normalmente classificada como hard rock e considerada uma das pioneiras do heavy metal.'),
(17, 05, 'Sepultura', 'https://s-media-cache-ak0.pinimg.com/originals/e0/2d/a4/e02da4cee2531bdcc0c64996d0c36a45.jpg', 'Sepultura é uma banda brasileira de metal. É considerada a banda brasileira de maior repercussão no mundo. O Sepultura possui influências diversificadas, que vão desde o black metal e death metal, passando pelo thrash metal, até inspirações externas ao metal, como hardcore, nu metal, música tribal africana e japonesa, música indígena, entre outros diversos estilos musicais.');
INSERT INTO `bandas` (`id`, `datas_id`, `nome`, `url_imagem`, `descricao`) VALUES
(18, 05, 'Ramones', 'data:image/png;base64,', 'Ramones foi uma banda norte-americana de punk rock formada em Forest Hills, no distrito de Queens, Nova York, no ano de 1974. Considerada como precursora do estilo e uma das bandas mais influentes e importantes da história do rock.\r\nAviso: banda não mais existente, mas será exibido um cover da banda em homenagem.'),
(19, 05, 'Detonautas', 'https://lh3.googleusercontent.com/-Lqd8Ii_OEiE/UQvGivaILhI/AAAAAAAAABM/eJ7fKSkeyFk/s630-fcrop64=1,000038ceff10a78c/53.jpg', 'Detonautas Roque Clube (conhecida simplesmente por Detonautas) é uma banda brasileira de rock.Influenciados pelo som do gênero americano, geralmente as letras de suas canções referem-se a amor, violência e corrupção, uma preocupação com a cidadania que marca o perfil de luta social e política da banda.');

